   parent: rotate,
   xyz: [-5, 0, 0] // Translate along -X axis
});

var translate2 = new xeogl.Translate({
   parent: rotate,
   xyz: [5, 0, 0] // Translate along +X axis
});

var scale = new xeogl.Scale({
   parent: translate2,
